mapper.select:
SELECT b.name, a.uid, a.title, m.Nickname, a.created_date, a.hits FROM Article as a JOIN Board as b ON a.board_uid = b.uid JOIN Member as m ON a.member_uid = m.uid
WHERE a.board_uid = #{board_uid}
ORDER BY uid DESC Limit 0, 1000

mapper.update:
UPDATE
    Comment
SET
    comment = #{comment}
WHERE
    uid = #{uid}
